export const PRE_REQUEST = 'PRE_REQUEST';
export const POST_REQUEST = 'POST_REQUEST';

export const getProducts = (products) => ({
  type: 'ADD_PRODUCTS',
  data: products,
});

export const requestPosts = () => ({
  type: PRE_REQUEST,
});

export const receivedPosts = json => ({
  type: POST_REQUEST,
});

export function fetchProducts(page = 1, limit = 10) {
  return function (dispatch) {
  dispatch(PRE_REQUEST)
   return fetch(`https://backendapi.turing.com/products?page=${page}&limit=${limit}&description_length=200`)
      .then(response => response.json())
      .then((json) => {
        dispatch(getProducts(json.rows));

      })
      .catch( error => console.log('An error occurred.', error),);
  };
}
