import React from 'react';
import { connect } from 'react-redux';
import { sortByAmount, sortByDefault } from '../actions/FilterActions';

export class ProductListFilter extends React.Component {

  onSortChange = (e) => {
    if (e.target.value === 'default') {
      this.props.sortByDefault();
    } else if (e.target.value === 'amount') {
      this.props.sortByAmount();
    }
  };

  render() {
    return (
      <div>
        <select
          value={props.filters.sortBy} onChange={this.onSortChange}>
          <option value="default">Default</option>
          <option value="amount">Amount</option>
        </select>
      </div>
    );
  }
};

const mapStateToProps = (state) => ({
  filters: state.filters
});

const mapDispatchToProps = (dispatch) => ({
  sortByDefault: () => dispatch(sortByDefault()),
  sortByAmount: () => dispatch(sortByAmount()),
});

export default connect(mapStateToProps, mapDispatchToProps)(ProductListFilter);
